import styled from "styled-components";

export const StyledContact = styled.div`
  height: 80vh;

  h1 {
    font-size: 90px;
    text-align: center;
    color: #fefefe;
  }

  h3 {
    color: #fefefe;
    display: flex;
    justify-content: center;
    text-align: center;
  }
`;

export const StyledForm = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 70px;

  form input {
    height: 40px;
    width: 500px;
    margin: 10px;
    border-radius: 5px;
    text-align: center;
  }
`;

export const StyledSend = styled.div`
  input {
    background-color: transparent
  color: darken($darkblue, 10%)

  &:hover
    background-color: transparent
    box-shadow: none
    color: $offwhite

  &::before
    content: ""
    position: absolute
    top: 0
    left: 0
    width: 100%
    height: 100%
    border: 3px solid darken($darkblue, 10%)
    transition: opacity 0.3s, border 0.3s

  &:hover::before
    opacity: 0

  &::after
    content: ""
    position: absolute
    top: 0
    left: 0
    width: 100%
    height: 100%
    background-color: transparent
    border: 3px solid $offwhite
    opacity: 0
    z-index: -1
    transform: scaleX(1.1) scaleY(1.3)
    transition: transform 0.3s, opacity 0.3s

  &:hover::after
    opacity: 1
    transform: scaleX(1) scaleY(1)
  } 
  /* {
    text-align: center;
    color: #fefefe;
    background-color: #1a1d23;
    border-width: 1px;
    border-color: #0000;
    cursor: pointer;
    border: solid white 1px;
  } */
`;
