import styled from "styled-components";

export const StyledScroll = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;

  p {
    font-size: 20px;
    color: #fefefe;
  }

  img {
    margin-left: 10px;
    height: 30px;

    background-color: #1a1d23;
    border-color: green;
    color: #fefefe;
    transition: all 0.5s;
    border-radius: 30%;
    &:hover {
      border-radius: 30%;
      box-shadow: inset 18em 0 0 0 #f5cad4, inset -18em 0 0 0 #f5cad4;
      border-color: #f5cad4;
      color: #1a1d23;
      background-color: #f5cad4;
    }
  }
`;
