import styled from "styled-components";

export const StyledSocial = styled.div`
  /* background-color: ${({ theme }) => theme.colors.div}; */
  color: #fefefe;
  display: flex;
  justify-content: center;

  div {
    display: flex;
    justify-content: right;
    align-self: center;
    padding: 0 5px;
    margin: 0 5px;
  }

  img {
    height: 2em;
    align-self: right;
  }
`;
