  align-items: center;

  height: 75vh;
  border: solid white;

  h1 {
    display: flex;
    justify-content: center;
    color: #fefefe;
    font-size: 95px;
    border: solid white;
    /* margin-top: 20px; */
  }

  p {
    color: #fefefe;
    font-size: 25px;
    width: 500px;
    text-align: center;
  }

  h3 {
