import styled from "styled-components";
import keyboardIcon from "../../assets/keyboardIcon.svg";

export const StyledNav = styled.div`
  color: #fefefe;
  display: flex;
  flex-direction: column;
  justify-content: center;
  height: 100vh;
  width: 175px;
  position: fixed;
  left: 0;
  top: 0;
  background-color: #1a1d23;

  div {
    display: flex;
    justify-content: center;
    align-self: center;

    a {
      text-decoration: none;
    }
  }

  p {
    font-size: 25px;
    margin: 10px;
  }

  img {
    height: 30px;
    align-self: center;
  }

  .homeIcon img {
    height: 120px;
    position: absolute;
    top: 25px;
    left: 25px;
    /* border: solid white; */
  }

  .socialIcons {
    position: absolute;
    bottom: 25px;

    img {
      margin: 10px;
    }
  }
`;

export const MainContainer = styled.div`
  color: #fefefe;
  height: 100vh;
  width: 175px;
  position: fixed;
  left: 0;
  top: 0;
  background-color: #1a1d23;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

export const LogoContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
`;

export const Logo = styled.div`
  height: 120px;
  width: 120px;
  background-image: url(${keyboardIcon});
`;

export const LinksContainer = styled.div`
  background: blue;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export const LinkBox = styled.div`
  display: flex;
  flex-direction: column;

  :not(:last-child) {
    margin-bottom: 5px;
  }
`;

export const Links = styled.h2`
  font-size: 24px;
  line-height: 24px;
  // background: red;
  margin-bottom: 4px;
`;

export const LinkUnderline = styled.div`
  height: 2px;
  width: 100%;
  background: #fefefe;
`;

export const SocialContainer = styled.div`
  background: green;
  height: 50px;
  width: 100%;
`;
