import { StyledScroll } from "./styles/ScrollDown.styled";
import arrowIcon from "../assets/arrowIcon.svg";
import { motion } from "framer-motion";

const arrowVariant = {
  hidden: {
    y: "-10px",
    opacity: 0,
  },
  visible: {
    y: 0,
    opacity: 1,
    transition: {
      repeat: Infinity,
      duration: 1,
    },
  },
  hover: {
    scale: 1.5,
  },
};

const ScrollDown = () => {
  return (
    <StyledScroll>
      <p>Scroll down</p>
      <motion.img
        variants={arrowVariant}
        initial="hidden"
        animate="visible"
        whileHover="hover"
        src={arrowIcon}
        alt="arrow"
      />
    </StyledScroll>
  );
};

export default ScrollDown;
