import { StyledScroll } from "./styles/ScrollDown.styled";
import arrowIcon from "../assets/arrowIcon.svg";
import { motion } from "framer-motion";

const arrowVariant = {
  hidden: {
    y: "10vh",
  },
  visible: {
    y: "-10vh",
    transition: {
      repeat: Infinity,
    },
  },
};

const ScrollDown = () => {
  return (
    <StyledScroll>
      <p>Scroll down</p>
      <motion.image v src={arrowIcon} alt="arrow" />
    </StyledScroll>
  );
};

export default ScrollDown;
