import { StyledHome } from "./styles/Home.styled";
import heroIcon from "../assets/heroIcon.svg";
import ScrollDown from "./ScrollDown";
import { motion } from "framer-motion";

const greetingVariant = {
  hidden: {
    y: "20vh",
    opacity: 0,
  },
  visible: {
    y: 0,
    opacity: 1,
    transition: {
      duration: 1,
    },
  },
};

const heroVariant = {
  hidden: {
    y: "-20vh",
    opacity: 0,
  },
  visible: {
    y: 0,
    opacity: 1,
    transition: {
      duration: 1,
    },
  },
};

const Home = () => {
  return (
    <>
      <StyledHome id="home-section">
        <motion.div
          variants={greetingVariant}
          initial="hidden"
          animate="visible"
          id="home-section"
        >
          <div className="hello">
            <motion.h1>Hello,</motion.h1>
            {/* <img src={keyboardIcon} alt="keyboard" /> */}
          </div>
          <motion.h1
          // variant={greetingVariant}
          // initial="hidden"
          // animate="visible"
          >
            I am Jorge
          </motion.h1>
          <h5>I design and build things for the web.</h5>
        </motion.div>
        <motion.div variants={heroVariant} initial="hidden" animate="visible">
          <img src={heroIcon} alt="person doing work on couch with dog" />
        </motion.div>
      </StyledHome>
      <ScrollDown />
    </>
  );
};

export default Home;
