import styled from "styled-components";

export const StyledNav = styled.div`
  /* background-color: ${({ theme }) => theme.colors.div}; */
  color: #fefefe;
  display: flex;
  flex-direction: column;
  justify-content: center;
  /* border: solid red; */
  /* max-height: 70px; */
  /* overflow: hidden; */
  height: 100vh;
  width: 150px;
  border: solid white;
  position: absolute;
  left: 0;

  div {
    display: flex;
    justify-content: center;
    align-self: center;
    padding: 0 5px;
    margin: 0 5px;

    a {
      text-decoration: none;
    }
  }

  p {
    font-size: 25px;
  }

  img {
    height: 35px;
    align-self: center;
    margin-left: 10px;
  }

  .homeIcon img {
    height: 50px;
  }

  .socialIcons img {
    margin-left: 20px;
  }
`;
