import styled from "styled-components";

export const StyledProjects = styled.div`
  height: 80vh;
  border: solid white;

  h1 {
    display: flex;
    justify-content: center;
    color: #fefefe;
    font-size: 130px;
  }

  /* .projectsContainer {
    display: flex;
    justify-content: space-around;
  } */

  /* .pokeContainer,
