import styled from "styled-components";

export const StyledProjects = styled.div`
  height: 80vh;
  border: solid white;

  h1 {
    display: flex;
    justify-content: center;
    color: #fefefe;
    font-size: 130px;
  }

  h3 {
    color: #fefefe;
  }
`;

export const ProjectsContainer = styled.div`
  display: flex;
  justify-content: space-around;
`;

export const SingleProjectCont = styled.div`
  height: 700px;
  width: 450px;
  background-color: #1a1d23;
  border-radius: 25px;

  h1 {
    font-size: 50px;
  }

  h1,
  h3 {
    margin: 0 auto;
    padding: 0 15px;
    text-align: center;
  }
`;

export const DemoBLock = styled.div`
  display: flex;
  margin: 0 20px;
  justify-content: space-around;

  div {
    background-color: #fefefe;
    height: 50px;
    width: 150px;
    margin-top: 20px;
    border-radius: 15px;
  }

  div p {
    margin: 0;
    margin-top: 10px;
    padding: 0;
    text-align: center;
    font-size: 25px;
  }
`;
