import styled from "styled-components";

export const StyledContact = styled.div`
  height: 80vh;

  h1 {
    font-size: 90px;
    text-align: center;
    color: #fefefe;
  }

  h3 {
    color: #fefefe;
    display: flex;
    justify-content: center;
    width: 40px;
    height: 100px;
    text-align: center;
  }
`;

export const StyledForm = styled.div`
  display: flex;
  justify-content: center;

  form input {
    height: 40px;
    /* text-align: center; */
  }
`;

export const StyledSend = styled.div`
  text-align: center;
`;
