    font-size: 130px;
  }

  h3 {
    color: #fefefe;
  }
`;

export const ProjectsContainer = styled.div`
  display: flex;
  justify-content: space-around;
`;

export const SingleProjectCont = styled.div`
  height: 500px;
  width: 350px;
  background-color: #1a1d23;
  /* background-color: #000000; */
  border-radius: 25px;
  /* border: solid white; */

  img {
    height: 50%;
