import { createGlobalStyle } from "styled-components";

const GlobalStyles = createGlobalStyle`
    * {
        box-sizing: border-box;
    }

    body {
        /* background: ${({ theme }) => theme.colors.body}; */
        height: 200vh;
        font-size: 1.15em;
        background: url(./darkDot.png);

    }

`;

export default GlobalStyles;
