import styled from "styled-components";

export const StyledNav = styled.div`
  /* background-color: ${({ theme }) => theme.colors.div}; */
  color: #fefefe;
  display: flex;
  flex-direction: column;
  justify-content: center;
  /* border: solid red; */
  /* max-height: 70px; */
  /* overflow: hidden; */
  height: 100vh;
  width: 175px;
  border: solid white;
  position: fixed;
  left: 0;
  background-color: #1a1d23;

  div {
    display: flex;
    justify-content: center;
    align-self: center;
    border: solid white 0.5px;

    a {
      text-decoration: none;
    }
  }

  p {
    font-size: 25px;
  }

  img {
    height: 35px;
    align-self: center;
  }

  .homeIcon img {
    height: 120px;
    position: absolute;
    top: 20px;
    left: 25x;
    border: solid white;
  }
`;
